sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	// only call server on actual change
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

   // instantiate GetEmailAdress script include
	var getMail = new GlideAjax('GetEmailAddress');
	// specify method of script include
	getMail.addParam('sysparm_name', 'getEmail');
	// pass 'Requested for' sys_id
	getMail.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	// fire request to server and insert callback for asynchronous return
	getMail.getXML(populateEmail);

	// callback function for server answer
	function populateEmail(response) {
		// extract email from response
		var email = response.responseXML.documentElement.getAttribute("answer");
		// clear any value from email field
		g_form.clearValue('u_requested_email');
		// set new email value
		g_form.setValue('u_requested_email', email);
	}
}
