sys_script_include.script:
function validateEmailAdress (email) {

	// use JavaScript coercion to guarantee email is a string
	email = email + '';

	// compare email against allowed syntax w/ regex
	// return true if email is a correct email address, otherwise return false
	if (email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
		return true;
	} else {
		return false;
	}
}
