sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(date_passed) {
		// create GlideDateTime objects for current date and param date
		var now = new GlideDateTime();
		var date = new GlideDateTime(date_passed);

		// test passed date and return result
		if (date.before(now)) {
			return true;
		} else {
			return false;
		}
	},

	isDateToday: function(date_passed) {
		// create GlideDateTime objects for current date and param date
		var now = new GlideDateTime();
		var date = new GlideDateTime(date_passed);

		// extract date from timestamps
		var today = now.getLocalDate();
		var day = date.getLocalDate();

		// test for the same date and return result
		if (today.compareTo(day) == 0) {
			return true;
		} else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
